import React, { useEffect } from "react";
import { useRoutes } from "react-router-dom";
import ReadPosts from "./ReadPosts";
import CreatePost from "./CreatePost";
import EditPost from "./EditPost";
import { Link } from "react-router-dom";
import minionFactory from "/Users/junior/Desktop/randos/web102_unit7lab-main/client/src/components/assets/minionsFactory.jpeg";

const Home = () => {
  // Sets up routes
  let element = useRoutes([
    {
      path: "/",
      element: <ReadPosts />,
    },
    {
      path: "/edit/:id",
      element: <EditPost />,
    },
    {
      path: "/new",
      element: <CreatePost />,
    },
    {
      path: "/home",
      element: <Home />,
    },
  ]);

  return (
    <div className="App">
      <div className="header">
        <h1>⚠️Minion Factory⚠️</h1>
        <img src={minionFactory} />
        <Link to="/">
          <button className="headerBtn"> View Minions</button>
        </Link>
        <Link to="/home">
          <button className="headerBtn"> Home </button>
        </Link>
        <Link to="/new">
          <button className="headerBtn"> Create A Minion </button>
        </Link>
      </div>
      {element}
    </div>
  );
};

export default Home;
